import React, {useState, useEffect} from 'react';
import Button from 'react-bootstrap/Button';
import './ToDoList.css'

export default function OurTimer() {
  const [start, setStart] = useState('false');
  const [work, setWork] = useState('true');
  const [minutes, setMinutes] = useState('25');
  const [seconds, setSeconds] = useState('00');

  const handleStart = () => setStart((prev)=>(!prev));

  let startStatus = (start ? 'STOP' : 'START');
  let workStatus = (work ? 'REST' : 'WORK');

  return (

    <section>

        <section className="timerTimer">
          <div>
            <div className = 'buttons'>
              <Button variant="secondary" >{startStatus}</Button>{' '}
              <Button variant="secondary" >{workStatus}</Button>{' '} 
            </div> 
            <h1 className='timerMargin'>{minutes + ':' + seconds}</h1>
          </div>

      </section> 
    </section>
  )
}
