  //         }))
  //     }, 1000)
  // } else {
  //   this.setState(prevState=>({prevState}))
  // }
}

componentDidUpdate(){
  // const startCount = this.state.seconds
  // this.setState({
  //     seconds: startCount
  // })
  if (this.state.started === true) {
    this.interval = setInterval(()=>{
      this.setState(prevState =>({
          seconds: prevState.seconds - 1
          }))
      }, 1000)
  } 
}


componentWillUnmount(){
    clearInterval(this.interval)
}

  render () {
    let startStatus = (this.state.started ? 'STOP' : 'START');
    let workStatus = (this.state.work ? 'REST' : 'WORK');
    const seconds = this.state.seconds;
    const time = this.state.minutes + ":" + this.state.seconds

    return (

      <section>

          <section className="timerTimer">
            <div>
              <div className = 'buttons'>
                <Button onClick={()=>this.handleStart()} variant="secondary" >{startStatus}</Button>{' '}
                <Button onClick={()=>this.handleRest()} variant="secondary" >{workStatus}</Button>{' '} 
              </div> 
              <h1 className='timerMargin'>{time}</h1>
            </div>

        </section> 
      </section>
    )
  }


}

  // exporting component
  export default OurTimer


