import React, {useState, useEffect} from 'react';
import Button from 'react-bootstrap/Button';
import './ToDoList.css'

export default function OurTimer() {
  const [start, setStart] = useState(false);
  const [work, setWork] = useState(true);
  const [minutes, setMinutes] = useState(25);
  const [seconds, setSeconds] = useState(0);

  //function for decrementing timer
  const decrement = () => {
    if (start === true) {
      const intervalId = setInterval(() => {
        setSeconds((prev) => prev - 1);
      }, 1000);
      return () => {
        clearInterval(intervalId);
      };
    }
  }

  //calls decrement every time start changes
  useEffect(decrement, [start]);


  //function for adjusting display time
  const time = () => {
    if (start === true){
      if (seconds === 0 && minutes !== 0) {
        setSeconds(59);
        setMinutes((prev) => prev - 1);
      } else if (minutes === 0 && work === true ) {
        setMinutes(5);
        setSeconds(0);
        setWork(false);
      } else if (minutes === 0 && work === false) {
        setMinutes(25);
        setSeconds(0);
        setWork(true);
      }
    }
  }

  useEffect(time);

  let startStatus = (start ? 'STOP' : 'START');
  let workStatus = (work ? 'REST' : 'WORK');

  const handleStart = () => setStart(!start);

  return (

    <section>

        <section className="timerTimer">
          <div>
            <div className = 'buttons'>
              <Button variant="secondary" onClick={handleStart} >{startStatus}</Button>{' '}
              <Button variant="secondary" >{workStatus}</Button>{' '} 
            </div> 
            <h1 className='timerMargin'>{minutes + ':' + seconds}</h1>
          </div>

      </section> 
    </section>
  )
}
